    var stateParams = widgetContext.stateController
        .getStateParams();
    var params = {
        entityId: entityId,
        entityName: entityName
    };

    if (stateParams.city) {
        params.city = stateParams.city;
    }

    widgetContext.stateController.openState(statedId,
        params, false);
}

